import { useState, useEffect } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";

function AppointmentList(props) {
  const [visits, setVisits] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchVisits();
  }, []);

  const fetchVisits = async () => {
    setIsLoading(true);
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setIsLoading(false);
        return;
      }
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
      const { data: user } = await axios.post(
        "http://localhost:3002/users/verify",
        {},
        config
      );
      props.setUser(user);
      const { data: visits } = await axios.get(
        "http://localhost:3002/visits",
        config
      );
      if (user.role === "Admin") {
        setVisits(visits);
      } else if (user.role === "Customer") {
        setVisits(data.filter(visits, (elem) => elem.customer.id === user.id));
      } else {
        setVisits(_.filter(visits, (elem) => elem.agent.id === user.id));
      }
    } catch (err) {
      setError(err);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }
  if (error) {
    return <div className="App">{`Error: ${error.message}`}</div>;
  }
  if (!props.user.name) {
    return <Redirect to="/login" />;
  }

  return (
    <div className="content">
      <div className="container-fluid">
        <div className="row">
          <h3>
            Hello {props.user.name} {props.user.surname}
          </h3>
        </div>
        <div className="row">
          <div>
            <div className="content">
              <table class="table table-hover table-striped">
                <thead>
                  <tr>
                    <th>Visit Date</th>
                    <th>Customer</th>
                    <th>Agent</th>
                  </tr>
                </thead>
                <tbody>
                  {_.map(visits, (visit, index) => (
                    <tr key={index}>
                      <td>{visit.date}</td>
                      <td>
                        {visit.customer.name} {visit.customer.surname}
                      </td>
                      <td>
                        {visit.agent.name} {visit.agent.surname}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default AppointmentList;
