import { useEffect, useState } from "react";
import React from "react";
import VirtualDimensionsContainer from "./VirtualDimensionsContainer";
import { Redirect } from "react-router-dom";
import axios from "axios";

function Dashboard({ setUser, user }) {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .post("http://localhost:3002/users/verify", { token })
        .then(({ data }) => {
          setUser(data);
          setIsLoading(false);
        })
        .catch((error) => {
          setError(error);
          setIsLoading(false);
        });
    } else {
      setIsLoading(false);
    }
  }, [setUser]);

  if (!user.name) {
    return <Redirect to="/login" />;
  }

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  if (error) {
    return <div className="App">{error.message}</div>;
  }

  if (!user.name) {
    return <Redirect to="/login" />;
  }

  return (
    <div className="dashboard-page">
      <img className="background-image" src={require('../img/backgroundimage.jpg')} alt="Virtual Tour" />
      <div className="container text-container">
        <p className="paragraph">
          Welcome to Virtual Dimensions, where we specialize in creating virtual tours that transport you to a whole new world. Our tours are designed to be interactive and immersive, giving you the feeling of being right there in the scene. From historical landmarks to natural wonders, we have something for everyone. Explore the depths of the ocean, walk through ancient ruins, or take a tour of a city from the comfort of your own home. Experience the world in a whole new way with Virtual Dimensions.
        </p>
      </div>
      <div className="container table-container">
        <table className="table">
          <thead>
            <tr>
              <th className="table-header">Command number</th>
              <th className="table-header">Link</th>
              <th className="table-header">Time of post</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
            </tr>
            <tr>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
            </tr>
            <tr>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
              <td className="table-data">No info</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
export default Dashboard;
