import { useEffect, useState } from "react";
import React from "react";
import VirtualDimensionsContainer from "./VirtualDimensionsContainer";
import { Redirect } from "react-router-dom";
import axios from "axios";

function Dashboard({ setUser, user }) {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .post("http://localhost:3002/users/verify", { token })
        .then(({ data }) => {
          setUser(data);
          setIsLoading(false);
        })
        .catch((error) => {
          setError(error);
          setIsLoading(false);
        });
    } else {
      setIsLoading(false);
    }
  }, [setUser]);

  if (!user.name) {
    return <Redirect to="/login" />;
  }

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }

  if (error) {
    return <div className="App">{error.message}</div>;
  }

  if (!user.name) {
    return <Redirect to="/login" />;
  }

  return (
    <div className="content">
      <div className="container-fluid">
        <div className="row">
          <h3>
            Bonjour {user.name} {user.surname},
          </h3>
          <div className="content">
            <div className="row">
              <VirtualDimensionsContainer />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Dashboard;
